var handleSlimScroll = function() {
    "use strict";
    $("[data-scrollbar=true]").each(function() {
        generateSlimScroll($(this))
    })
};
var generateSlimScroll = function(e) {
    var t = $(e).attr("data-height");
    t = !t ? $(e).height() : t;
    $(e).slimScroll({
        height: t,
        alwaysVisible: true
    })
};
var handleSidebarMenu = function() {
    "use strict";
    $(".sidebar .nav > .has-sub > a").click(function() {
        var e = $(this).next(".sub-menu");
        var t = ".sidebar .nav > li.has-sub > .sub-menu";
        $(t).not(e).slideUp(250);
        $(e).slideToggle(250)
    });
    $(".sidebar .nav > .has-sub .sub-menu li.has-sub > a").click(function() {
        var e = $(this).next(".sub-menu");
        $(e).slideToggle(250)
    })
};
var handleMobileSidebarToggle = function() {
    var e = false;
    $(".sidebar").click(function(t) {
        if ($(t.target).closest(".sidebar").length !== 0) {
            e = true
        } else {
            e = false;
            t.stopPropagation()
        }
    });
    $(document).click(function(t) {
        if ($(t.target).closest(".sidebar").length === 0) {
            e = false
        }
        if (!t.isPropagationStopped() && e !== true) {
            if ($("#page-container").hasClass("page-sidebar-toggled")) {
                $("#page-container").removeClass("page-sidebar-toggled")
            }
            if ($(window).width() < 979) {
                if ($("#page-container").hasClass("page-with-two-sidebar")) {
                    $("#page-container").removeClass("page-right-sidebar-toggled")
                }
            }
        }
    });
    $("[data-click=right-sidebar-toggled]").click(function(e) {
        e.stopPropagation();
        var t = "#page-container";
        var n = "page-right-sidebar-collapsed";
        n = $(window).width() < 979 ? "page-right-sidebar-toggled" : n;
        if ($(t).hasClass(n)) {
            $(t).removeClass(n)
if($("#han-page-container").hasClass("in")==false) //키워드 검색중일 경우
			{
				$("#han-page-loader").removeClass("hide");
			}
			else if($("#han-page-container").hasClass("in")==true) //키워드 검색중이 아닌 경우
			{
				$("#han-page-loader").addClass("hide");
			}
        } else {
            $(t).addClass(n)
$("#han-page-loader").addClass("hide");
        } if ($(window).width() < 480) {
            $("#page-container").removeClass("page-sidebar-toggled")
        }
    });
    $("[data-click=sidebar-toggled]").click(function(e) {
        e.stopPropagation();
        var t = "page-sidebar-toggled";
        var n = "#page-container";
        if ($(n).hasClass(t)) {
            $(n).removeClass(t)
        } else {
            $(n).addClass(t)
        } if ($(window).width() < 480) {
            $("#page-container").removeClass("page-right-sidebar-toggled")
        }
    })
};
var handleSidebarMinify = function() {
    $("[data-click=sidebar-minify]").click(function(e) {
        e.preventDefault();
        var t = "page-sidebar-minified";
        var n = "#page-container";
        if ($(n).hasClass(t)) {
            $(n).removeClass(t);
            if ($(n).hasClass("page-sidebar-fixed")) {
                generateSlimScroll($('#sidebar [data-scrollbar="true"]'))
            }
        } else {
            $(n).addClass(t);
            if ($(n).hasClass("page-sidebar-fixed")) {
                $('#sidebar [data-scrollbar="true"]').slimScroll({
                    destroy: true
                });
                $('#sidebar [data-scrollbar="true"]').removeAttr("style")
            }
            $("#sidebar [data-scrollbar=true]").trigger("mouseover")
        }
        $(window).trigger("resize")
    })
};
var handlePageContentView = function() {
    "use strict";
    $(window).load(function() {
        $.when($("#page-loader").addClass("hide")).done(function() {
            $("#page-container").addClass("in");
$("#han-page-loader").addClass("hide");
			$("#han-page-container").addClass("in");
        })
    })
};

var handelTooltipPopoverActivation = function() {
    "use strict";
    $("[data-toggle=tooltip]").tooltip();
    $("[data-toggle=popover]").popover()
};
var handleScrollToTopButton = function() {
    "use strict";
    $(document).scroll(function() {
        var e = $(document).scrollTop();
        if (e >= 200) {
            $("[data-click=scroll-top]").addClass("in")
        } else {
            $("[data-click=scroll-top]").removeClass("in")
        }
    });
    $("[data-click=scroll-top]").click(function(e) {
        e.preventDefault();
        $("html, body").animate({
            scrollTop: $("body").offset().top
        }, 500)
    })
};

var handleAfterPageLoadAddClass = function() {
    if ($("[data-pageload-addclass]").length !== 0) {
        $(window).load(function() {
            $("[data-pageload-addclass]").each(function() {
                var e = $(this).attr("data-pageload-addclass");
                $(this).addClass(e)
            })
        })
    }
};
var App = function() {
    "use strict";
    return {
        init: function() {
            handleSlimScroll();
            handleSidebarMenu();
            handleMobileSidebarToggle();
            handleSidebarMinify();
            handleAfterPageLoadAddClass();
            handelTooltipPopoverActivation();
            handleScrollToTopButton();
            handlePageContentView()
        }
    }
}()