char* msg[3];
for(int i = 0; i &lt; 3; i++)
{
  char szBuf[4] = {0,};
  sprintf(szBuf, "%d", i);
  msg[i] = szBuf;
} for(int i = 0; i &lt; 3; i++)
{
  printf("%s", msg[i]);
}

